mapper.select:
select a.position_id, a.user_id, a.position_type, a.position_status, a.work_type, a.position_name, a.position_description, a.job_category, a.degree_lower_limit, a.experience_lower_limit, a.salary_lower_limit, a.salary_upper_limit, a.salary_month, a.work_place, a.is_oversea, a.oversea_remainder, a.position_num, a.update_time, a.reason, b.user_type, b.account, d.name, d.title, d.company_id, d.vip_type, d.real_name, c.company_id, c.company_name, c.industry, c.scale, c.enterprise_certified, e.ai_examiner, e.ai_examiner_desc, e.oversea_remainder, e.resume_threshold, e.degree_lower_limit as tool_degree_lower_limit, e.experience_lower_limit as tool_experience_lower_limit, e.experience_upper_limit as tool_experience_upper_limit, e.age_lower_limit, e.age_upper_limit,e.college, e.major, e.gender, e.residentia_address, f.id, f.key_word from ( select * from t_position tp where position_status = 2
and EXISTS ( SELECT 1 FROM t_position_key_word k WHERE k.position_id = tp.position_id AND k.key_word = #{keyWord} )
and EXISTS ( select 1 from t_hr m1 inner join t_company m2 on m1.company_id = m2.company_id where m1.user_id = tp.user_id and m2.scale = #{scale} )
and position_name like concat('%',#{positionName},'%')
and work_type = #{workType}
and salary_lower_limit >= #{salaryLowerLimit}
and salary_upper_limit <= #{salaryUpperLimit}
and experience_lower_limit >= #{experienceLowerLimit}
and degree_lower_limit >= #{degreeLowerLimit}
and job_category = #{jobCategory}
and position_type = #{positionType}
order by update_time desc
limit ${(currentPage-1) * pageSize}, #{pageSize} ) a left join t_user b on a.user_id = b.user_id left join t_hr d on a.user_id = d.user_id left join t_company c on d.company_id = c.company_id left join t_position_toolbox e on a.position_id = e.position_id left join t_position_key_word f on a.position_id = f.position_id order by a.update_time desc

SELECT , COUNT(b.position_id) AS newResumeNum, min(reason) as reason FROM t_position a LEFT JOIN t_hr_mark_resume b ON a.position_id = b.position_id and b.hr_user_id = #{userId} AND b.resume_status = 'new_message'
a.user_id = #{userId}
and a.position_status = #{positionStatus}
GROUP BY
order by a.update_time desc

select a.position_id, a.position_type , a.position_name, a.position_status, a.update_time, a.job_category, c.company_name, b.name as hr_name from t_position a left join t_hr b on a.user_id = b.user_id left join t_company c on b.company_id = c.company_id
and a.position_status = #{positionStatus}
order by a.update_time desc
limit ${(currentPage-1) * pageSize}, #{pageSize}
